/**
 * Generates a PDF document from the provided trainer tasks.
 * This function relies on the jsPDF library, which must be loaded separately.
 * @param {object} trainerSettings - The settings object containing title, etc.
 * @param {Array} tasks - The array of task objects from the trainer's state.
 * @param {Function} isAnswerCorrect - The function to calculate the correct answer for a task.
 */
function generatePDF(trainerSettings, tasks, isAnswerCorrect) {
    // Check if jsPDF library is loaded
    if (typeof window.jspdf === 'undefined') {
        alert("Ошибка: Библиотека для создания PDF (jsPDF) не загружена.");
        return;
    }
    const { jsPDF } = window.jspdf;
    const doc = new jsPDF();

    // --- BLOCK FOR CYRILLIC SUPPORT ---

    // 1. Paste the very long Base64 string from the converter here.
    // It must be inside the backticks.
    const font = `Фу`;

    // 2. Register the font in jsPDF
    // Make sure the font variable is not empty before proceeding
    if (font.startsWith('PASTE')) {
        alert("Ошибка: Шрифт для PDF не встроен. Кириллица не будет отображаться.");
        doc.setFont('Helvetica'); // Fallback to a default font
    } else {
        doc.addFileToVFS('PTSerif-Regular.ttf', font);
        doc.addFont('PTSerif-Regular.ttf', 'PT Serif', 'normal');
        doc.setFont('PT Serif');
    }

    // --- END OF CYRILLIC SUPPORT BLOCK ---

    doc.setFontSize(18);
    doc.text(trainerSettings.title, 105, 20, { align: 'center' });

    let y = 40;
    tasks.forEach((task, index) => {
        if (y > 270) { // New page if there's no space left
            doc.addPage();
            y = 20;
        }

        // Remove HTML tags from the problem text for the PDF
        const tempDiv = document.createElement('div');
        tempDiv.innerHTML = task.generatedTask.problemText;
        const questionText = tempDiv.innerText;

        const question = `${index + 1}. ${questionText}`;
        // Calculate the correct answer to display it on the sheet
        const { correctAnswerText } = isAnswerCorrect('', task.originalTask, task.generatedTask.variables);
        const answer = `Ответ: ${correctAnswerText}`;

        doc.setFontSize(12);
        const splitQuestion = doc.splitTextToSize(question, 180); // Wrap long lines
        doc.text(splitQuestion, 15, y);
        y += (splitQuestion.length * 5) + 5;
        
        // --- НОВЫЙ БЛОК ---
        // 3. Получаем и добавляем ответ пользователя
        let userAnswer = task.input.value.trim(); // Берём значение из поля ввода
        if (userAnswer === '') {
            userAnswer = 'нет'; // Если поле пустое, пишем "нет"
        }
        doc.setTextColor(100, 100, 100); // Серый цвет для ответа пользователя
        doc.text(`Ваш ответ: ${userAnswer}`, 15, y);
        y += 7;
        // --- КОНЕЦ НОВОГО БЛОКА ---
        
        doc.setTextColor(0, 100, 0); // Green color for the answer
        doc.text(answer, 15, y);
        y += 15;
        doc.setTextColor(0, 0, 0); // Reset color to black
    });

    doc.save(`${trainerSettings.title.replace(/ /g, '_')}.pdf`);
}
